import { useReducer } from "react";

const CALC_OPTIONS = ["add", "minus", "divide", "multiply"];

const reducer = (prev, { type }) => {
  prev.type = type;
  switch (type) {
    case CALC_OPTIONS[0]:
      prev.result = prev.a + prev.b;
      return prev;

    case CALC_OPTIONS[1]:
      prev.result = prev.a - prev.b;
      return prev;

    default:
      throw new Error();
  }
};

const Example = () => {
  const initState = {
    a: 1,
    b: 2,
    result: 3,
    type: "add",
  };

  const [state, dispatch] = useReducer(reducer, initState);

  const calculate = (e) => {
    dispatch({ type: e.target.value });
  };

  const numChangeHandler = (e) => {};

  return (
    <>
      <h3>練習問題</h3>
      <p>useReducerを使って完成コードと同じ機能を作成してください。</p>
      <div>
        a:
        <input
          type="number"
          name="a"
          value={state.a}
          onChange={numChangeHandler}
        />
      </div>
      <div>
        b:
        <input
          type="number"
          name="b"
          value={state.b}
          onChange={numChangeHandler}
        />
      </div>
      <select value={state.type} onChange={calculate}>
        {CALC_OPTIONS.map((calc_option, index) => (
          <option value={index} key={index}>
            {calc_option}
          </option>
        ))}
      </select>
      <h1>結果：{state.result}</h1>
    </>
  );
};

export default Example;
